//to find the largest word in the string..
#include<bits/stdc++.h>
using namespace std;
void maxLenWord(string str, string &maxword){
	int len=str.length();int max_start;
	int i=0,j=0;
	int min_len=len,max_len=0;
	while(j<=len){
		if(j<=len && str[j] !=' ')
		j++;
		else{
			int curr_len=j-i;
			if(curr_len>max_len){
				max_len=curr_len;
				max_start=i;
			}
			j++;
			i=j;
		}
	} 
	maxword=str.substr(max_start,max_len);                                                                                                                               	                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                  
}

int main(){
	string str="Google docs is document in google";
	string maxword;
	maxLenWord(str,maxword);
	cout<<"Largest word is:" <<maxword<<endl;
	}

